import React from 'react';
import Avatar from './Avatar';

const AvatarList = () => {
  const avatars = [
    { id: 1, name: 'John Doe', imageUrl: 'https://vtv1.mediacdn.vn/zoom/640_400/2023/2/17/170223-selena-gomez-16766075094281157926610-crop-16766075158342089150803.jpg' },
    { id: 2, name: 'Jane Doe', imageUrl: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Bob Smith', imageUrl: 'https://example.com/bob.jpg' },
    { id: 4, name: 'Alice Johnson', imageUrl: 'https://example.com/alice.jpg' },
  ];

  return (
    <div className="avatar-list">
      {avatars.map((avatar) => (
        <Avatar key={avatar.id} name={avatar.name} imageUrl={avatar.imageUrl} />
      ))}
    </div>
  );
}

export default AvatarList;
